+        if (selectedFeature != null && !selectedFeature.getAttributes().get("TYPDAMAGE").equals(n)) {
+          selectedFeature.getAttributes().put("TYPDAMAGE", damageTypeComboBox.getValue());
+          updateFeature(selectedFeature);
