          if (ctx.request().isEnded()) {
            ctx.response().end("OK2"); // Vert.x 3.6 series
          } else {
            ctx.request().endHandler(y -> {
              ctx.response().end("OK2");
            });
            ctx.request().resume();
          }
        }).onFailure(cause -> {
          ctx.response().setStatusCode(500);
          ctx.response().end(cause.getMessage());
        });

