+        ctx.response().putHeader("Content-Type", "text/plain");
+        ctx.response().setStatusCode(500);
+        ctx.response().end(res.cause().getMessage());
+        return;
+      }
+      ctx.response().setStatusCode(204);
+      ctx.response().end();
+    });

