+        List<TableRef> tbls = rndTables(rndQryCtx.schema());
+
+        assert !F.isEmpty(tbls);
+
+        if (tbls.size() == 1 || rndWithRatio(5)) {
+            tbls.forEach(rndQryCtx::addScopeTable);
+
+            return new TableList(tbls);
+        }
+
+        TableRef leftTbl = tbls.get(0);
