    private SubnetworkList getSubnetworkList(Region region, Compute compute, String projectId, List<String> subnetIds,
        String sharedProjectId) throws IOException {
        String projectIdToQuery = StringUtils.isNotBlank(sharedProjectId) ? sharedProjectId : projectId;
        SubnetworkList subnetworkList = compute.subnetworks().list(projectIdToQuery, region.value()).execute();
        if (subnetIds.isEmpty()) {
            return subnetworkList;
        } else {
            List<Subnetwork> filteredSubnets = subnetworkList.getItems().stream()
                    .filter(subnetwork -> subnetIds.contains(subnetwork.getName()))
                    .collect(Collectors.toList());
            return new SubnetworkList().setItems(filteredSubnets);
        }
    }

