+
+    private Id id;
+    private GraphRole role;
+    private int load;
+    private Date updateTime;
+
+    public HugeServerInfo(String name, GraphRole role) {
+        this(IdGenerator.of(name), role);
+    }
+
+    public HugeServerInfo(Id id) {
+        this.id = id;
+        this.load = 0;
+        this.role = GraphRole.WORKER;
+        this.updateTime = DateUtil.now();
+    }
+
+    public HugeServerInfo(Id id, GraphRole role) {
+        this.id = id;
+        this.load = 0;
+        this.role = role;
+        this.updateTime = DateUtil.now();
+    }
+
+    public Id id() {
+        return this.id;
+    }
+
+    public String name() {
+        return this.id.asString();
+    }
+
+    public GraphRole role() {
+        return this.role;
+    }
+
+    public void role(GraphRole role) {
+        this.role = role;
+    }
+
+    public int load() {
+        return this.load;
+    }
+
+    public void load(int load) {
+        this.load = load;
+    }
+
+    public Date updateTime() {
+        return this.updateTime;
+    }
+
+    public void updateTime(Date updateTime) {
+        this.updateTime = updateTime;
+    }
+
+    public boolean alive() {
+        long now = DateUtil.now().getTime();
+        return this.updateTime != null &&
+               this.updateTime.getTime() + EXPIRED_INTERVAL > now;
+    }
+
+    @Override
+    public String toString() {
+        return String.format("HugeServerInfo(%s)%s", this.id, this.asMap());
+    }
+
+    protected boolean property(String key, Object value) {
+        switch (key) {
+            case P.ROLE:
+                this.role = SerialEnum.fromCode(GraphRole.class, (byte) value);
+                break;
+            case P.LOAD:
+                this.load = (int) value;
+                break;
+            case P.UPDATE_TIME:
+                this.updateTime = (Date) value;
+                break;
+            default:
+                throw new AssertionError("Unsupported key: " + key);
+        }
+        return true;
+    }
+
+    protected Object[] asArray() {
+        E.checkState(this.id != null, "Server id can't be null");
+
+        List<Object> list = new ArrayList<>(8);
+
+        list.add(T.label);
+        list.add(P.SERVER);
+
+        list.add(T.id);
+        list.add(this.id);
+
+        list.add(P.ROLE);
+        list.add(this.role.code());
+
+        list.add(P.LOAD);
+        list.add(this.load);
+
+        list.add(P.UPDATE_TIME);
+        list.add(this.updateTime);
+
+        return list.toArray();
+    }
+
+    public Map<String, Object> asMap() {
+        E.checkState(this.id != null, "Server id can't be null");
+
+        Map<String, Object> map = new HashMap<>();
+
+        map.put(Graph.Hidden.unHide(P.ID), this.id);
+        map.put(Graph.Hidden.unHide(P.LABEL), P.SERVER);
+        map.put(Graph.Hidden.unHide(P.ROLE), this.role);
+        map.put(Graph.Hidden.unHide(P.LOAD), this.load);
+        map.put(Graph.Hidden.unHide(P.UPDATE_TIME), this.updateTime);
+
+        return map;
+    }
+
+    public static HugeServerInfo fromVertex(Vertex vertex) {
+        HugeServerInfo server = new HugeServerInfo((Id) vertex.id());
+        for (Iterator<VertexProperty<Object>> iter = vertex.properties();
+             iter.hasNext();) {
+            VertexProperty<Object> prop = iter.next();
+            server.property(prop.key(), prop.value());
+        }
+        return server;
+    }
+
+    public <V> boolean suitableFor(HugeTask<V> task, long now) {
+        if (this.updateTime.getTime() + EXPIRED_INTERVAL < now ||
+            this.load() + task.load() > MAX_LOAD) {
+            return false;
+        }
+        return true;
+    }
+
+    public static Schema schema(HugeGraphParams graph) {
+        return new Schema(graph);
+    }
+
+    public static final class P {
+
