+            } while (page != null);
+        }
+
+        HugeServerInfo serverInfo = new HugeServerInfo(server, nodeRole);
+        serverInfo.maxLoad(this.calcMaxLoad());
+        this.serverId = serverInfo.id();
+        this.serverRole = nodeRole;

