+                                    break;
+                                case DOUBLE:
+                                    annotationValues.put(value.name(),
+                                            value.asDoubleArray());
+                                    break;
+                                case LONG:
+                                    annotationValues.put(value.name(),
+                                            value.asLongArray());
+                                    break;
+                                case BOOLEAN:
+                                    annotationValues.put(value.name(),
+                                            value.asBooleanArray());
+                                    break;
+                                case CLASS:
