+package com.marklogic.hub.hubcentral;
+
+import com.marklogic.hub.HubClient;
+import java.io.File;
+import java.io.FileOutputStream;
+import java.io.IOException;
+import java.util.Enumeration;
+import java.util.HashMap;
+import java.util.Map;
+import java.util.stream.Stream;
+import java.util.zip.ZipEntry;
+import java.util.zip.ZipFile;
+
+import static org.junit.jupiter.api.Assertions.assertEquals;
+import static org.junit.jupiter.api.Assertions.assertTrue;
+
+public class InitializedDataHubProject extends AllArtifactsProject{
+
+    private Map<String, Integer> zipProjectEntries;
+    private File zipProjectFile;
+
+    public InitializedDataHubProject(HubClient hubClient) {
+        super(hubClient);
+    }
+
+    public void writeProjectToZipFile() {
+        try {
+            zipProjectFile = new File("build/allProject.zip");
+            FileOutputStream fos = new FileOutputStream(zipProjectFile);
+            new HubCentralManager().writeProjectAsZip(hubClient, fos);
+            fos.close();
+            readZipProject();
+        } catch (IOException e) {
+            throw new RuntimeException(e);
+        }
+    }
+
+    private void readZipProject() throws IOException {
+        zipProjectEntries = new HashMap<>();
+        zipArtifactEntries = new HashMap<>();
+        ZipFile zip = new ZipFile(zipProjectFile);
+        Enumeration<?> entries = zip.entries();
+        String[] artifactDirs = {"flows", "steps", "entities", "src/main/entity-config", "src/main/ml-config/security/protected-paths",
+            "src/main/ml-config/security/query-rolesets"};
+        while (entries.hasMoreElements()) {
+            ZipEntry entry = (ZipEntry) entries.nextElement();
+            if(Stream.of(artifactDirs).anyMatch(entry.getName()::startsWith) && !entry.isDirectory()){
+                readArtifactZipEntry(zip, entry);
+            }
+            else{
+                String key;
+                if(!entry.isDirectory()){
+                    assertTrue(entry.getSize() > 0);
+                    int index = entry.getName().lastIndexOf('/');
+                    if(index == -1){
+                        key = "rootDir";
+                    }
+                    else{
+                        key = entry.getName().substring(0, entry.getName().lastIndexOf('/'));
+                    }
+                    zipProjectEntries.computeIfPresent(key, (k, v) -> v + 1);
+                }
+                else{
+                    key = entry.getName();
+                }
+                zipProjectEntries.putIfAbsent(key, 1);
+            }
+        }
+    }
+
+    public void verifyZipProject(){
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/custom/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/ingestion/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/mapping/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/mapping-functions/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/mastering/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/matching/"));
+        assertTrue(zipProjectEntries.containsKey("src/main/ml-modules/root/custom-modules/merging/"));
+        assertEquals(5, zipProjectEntries.get("rootDir"));
+        assertEquals(2, zipProjectEntries.get("src/main/hub-internal-config/database-fields"));

