+            @Override
+            protected void init()
+            {
+                super.init();
+
+                addButton(new Button(0, 0, 120, 20, new StringTextComponent("close"), btn -> {
+                    Minecraft.getInstance().displayGuiScreen(returnTo);
+                }));
+            }
+        });

