+    @Test
+    public void shouldRegenNullFileCacheEntries() throws IOException {
+        String srcMT = "text/html";
+        String fileName = "hello.html";
+        String converterName = cs.getConverterName(srcMT, "application/pdf");
+        assertEquals("any2pdf", converterName);
+
+        checkConverterAvailability(converterName);
+        checkCommandAvailability("soffice");
+
+        BlobHolder hg = getBlobFromPath("test-docs/" + fileName, srcMT);
+        Map<String, Serializable> parameters = new HashMap<>();
+
+        // first conversion
+        BlobHolder result = cs.convert(converterName, hg, parameters);
+        assertNotNull(result);
+
+        // retrieve the temp file
+        String cacheKey = CacheKeyGenerator.computeKey(converterName, hg, parameters);
+        BlobHolder blobHolder = ConversionCacheHolder.getFromCache(cacheKey);
+        String firstTempFileName = blobHolder.getBlob().getFile().getAbsolutePath();
+        // delete the temp file
+        Files.delete(Paths.get(blobHolder.getBlob().getFile().getPath()));
+
+        // replay conversion
+        cs.convert(converterName, hg, parameters);
+        blobHolder = ConversionCacheHolder.getFromCache(cacheKey);
+        String secondTempFileName = blobHolder.getBlob().getFile().getAbsolutePath();
+        // missing temp file has been regenerated. So it has a different name
+        assertNotEquals(firstTempFileName, secondTempFileName);
+    }
+
+}

