+                case WINDSPEED_CHANNEL:
+                    uid = 4;
+                    value = Integer.parseInt(command.toString());
+                    break;
+                case SWINGUD_CHANNEL:
+                    uid = 5;
+                    value = Integer.parseInt(command.toString());
+                    break;
+                case SWINGLR_CHANNEL:
+                    uid = 6;
+                    value = Integer.parseInt(command.toString());
+                    break;
+                case TEMP_CHANNEL:
