+    private void handleCommandLoudness(Command command, String id) {
+        if (command instanceof RefreshType) {
+            getLoudness("loudness".equals(id) ? UPNP_MASTER : id.replace("loudness", ""));
+        } else if (command instanceof OnOffType) {
