+    private void handleCommandStop(Command command) {
+        if (OnOffType.ON.equals(command)) {
+            updateState(CONTROL, PlayPauseType.PAUSE);
+            stop();
+            updateState(TRACK_POSITION, new QuantityType<>(0, SmartHomeUnits.SECOND));
+        }
+    }
