+            try {
+                BaseStatusInfo statusInfo = floureonDevice.getBasicStatus();
+                if (SENSOR_INTERNAL.equals(command.toFullString())) {
+                    floureonDevice.setMode(statusInfo.getAutoMode(), statusInfo.getLoopMode(), SensorControl.INTERNAL);
