+            String timestamp = getTimestamp(advancedStatusInfo);
+            logger.debug("Composed timestamp: {}", timestamp);
+            updateState(TIME, DateTimeType.valueOf(timestamp));
+        } catch (Exception e) {
+            logger.error("Error while retrieving data for {}", thing.getUID(), e);
