+
+        } catch (IOException e) {
+            throw new BuildException(e);
+        }
+    }
+
+    /**
+     * @param dir
+     * @param checksumsDir
+     * @throws IOException
+     */
+    private void generateZipChecksums(File dir, File checksumsDir) throws IOException {
+        checksumsDir.mkdirs();
+        File[] zipFiles = dir.listFiles();
+        for (File zipFile : zipFiles) {
+            System.out.println(zipFile.getName());
