+		handlePersonAddress("pad", city, state, postalCode, country).ifPresent(c -> {
+			criteria.createAlias("m.addresses", "pad");
+			criteria.add(c);
+		});
+		
+		handleSort(criteria, sort);
+		
+		return criteria.list();
+	}

