+package org.sonar.java.checks.regex;
+
+import java.text.Normalizer;
+import java.util.Collections;
+import javax.annotation.Nullable;
+import org.sonar.check.Rule;
+import org.sonar.java.regex.RegexParseResult;
+import org.sonar.java.regex.ast.CharacterClassTree;
+import org.sonar.java.regex.ast.CharacterClassUnionTree;
+import org.sonar.java.regex.ast.JavaCharacter;
+import org.sonar.java.regex.ast.PlainCharacterTree;
+import org.sonar.java.regex.ast.RegexBaseVisitor;
+import org.sonar.java.regex.ast.RegexTree;
+import org.sonar.plugins.java.api.tree.MethodInvocationTree;
+
+@Rule(key = "S5868")
+public class GraphemeClustersInClassesCheck extends AbstractRegexCheck {
+
+  private static final String MESSAGE = "Extract these Grapheme Clusters from the character class.";
+
+  @Override
+  public void checkRegex(RegexParseResult regexForLiterals, MethodInvocationTree mit) {
+    new GraphemeInClassVisitor().visit(regexForLiterals);
+  }
+
+  private class GraphemeInClassVisitor extends RegexBaseVisitor {
+
+    boolean containGrapheme = false;
+
+    @Override
+    public void visitCharacterClass(CharacterClassTree tree) {
+      super.visitCharacterClass(tree);
+      if (containGrapheme) {
+        reportIssue(tree, MESSAGE, null, Collections.emptyList());
+      }
+      containGrapheme = false;
+    }
+
+    @Override
+    public void visitCharacterClassUnion(CharacterClassUnionTree tree) {
+      JavaCharacter previousChar = null;
+      for (RegexTree child : tree.getCharacterClasses()) {
+        if (child.is(RegexTree.Kind.PLAIN_CHARACTER)) {
+          JavaCharacter currentCharacter = ((PlainCharacterTree) child).getContents();
+          if (!currentCharacter.isEscapedUnicode()) {
+            char currentChar = currentCharacter.getCharacter();
+            if (canCanonicallyCombine(previousChar, currentChar)) {
+              containGrapheme = true;
+            }
+            previousChar = currentCharacter;
+            continue;
+          }
+        }
+        previousChar = null;
+      }
+
+      super.visitCharacterClassUnion(tree);
+    }
+
+    private boolean canCanonicallyCombine(@Nullable JavaCharacter previousChar, char currentChar) {
+      return previousChar != null
+        && Normalizer.normalize(new StringBuilder().append(previousChar.getCharacter()).append(currentChar), Normalizer.Form.NFC).length() == 1;
