+                List<WebElement> responsesFields = getAllResponseFields(responseView);
+                if (isGiverVisible) {
+                    int giverIndex = getGiverIndex(responseView, response.getGiver());
+                    assertTrue(isResponseEqual(question, responsesFields.get(giverIndex), response));
