+        ArrayAccessExpr node = (ArrayAccessExpr) javaParserNode;
+        processArrayAccess(javacTree, node);
+        javacTree.getExpression().accept(this, node.getName());
+        javacTree.getIndex().accept(this, node.getIndex());
