+        for (Dependency dependency : dependencies) {
+            if (dependency.getType() != Dependency.Type.JS_MODULE) {
+                continue;
+            }
+
+            String url = dependency.getUrl();
+            String source = getSourcesFromTemplate(tag, url);
+            if (source == null) {
+                try {
