+    @Override
+    public void aggregate(final Configuration configuration, final List<LaunchResults> launchesResults,
+            final Path outputDirectory)
+    {
+        Map<String, String> payload = new HashMap<>();
+        CustomFields.STORIES.add(payload, Long.toString(countStories(launchesResults)));
+        CustomFields.STEPS.add(payload, Long.toString(countSteps(launchesResults)));
+        CustomFields.DURATION.add(payload, Long.toString(coundDuration(launchesResults)));
