+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkDates(getValueSafely(m, Message::getSentDate), rule, variable);
+        }
+    },
+    RECEIVED_DATE
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkDates(getValueSafely(m, Message::getReceivedDate), rule, variable);
+        }
+    },
+    FROM
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkAddressees(rule, split(variable), getValueSafely(m, Message::getFrom));
+        }
+    },
+    CC_RECIPIENTS
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkAddressees(rule, split(variable),
+                    getValueSafely(m, msg -> msg.getRecipients(RecipientType.CC)));
+        }
+    },
+    BCC_RECIPIENTS
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkAddressees(rule, split(variable),
+                    getValueSafely(m, msg -> msg.getRecipients(RecipientType.BCC)));
+        }
+    },
+    TO_RECIPIENTS
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkAddressees(rule, split(variable),
+                    getValueSafely(m, msg -> msg.getRecipients(RecipientType.TO)));
+        }
+    },
+    REPLY_TO
+    {
+        @Override
+        public Predicate<Message> createFilter(String rule, String variable)
+        {
+            return m -> checkAddressees(rule, split(variable), getValueSafely(m, Message::getReplyTo));
+        }
+    };
+
+    public abstract Predicate<Message> createFilter(String rule, String variable);
+
+    private static <T> T getValueSafely(Message message, MessagingExceptionFunction<Message, T> getter)
+    {
+        try
+        {
+            return getter.apply(message);
+        }
+        catch (MessagingException e)
+        {
+            throw new IllegalStateException(e);
+        }
+    }
+
+    private static List<String> split(String value)
+    {
