+
+public final class JsonResourceFinder
+{
+    private static final Logger LOGGER = LoggerFactory.getLogger(JsonResourceFinder.class);
+
+    private static final List<Character> CONTINUE_CHARACTERS = List.of(' ', '\n', '\r');
+    private static final List<Character> BREAK_CHARACTERS = List.of('{', '[');
+    private static final int BUFFER_SIZE = 8;
+
+    private JsonResourceFinder()
+    {
+    }
+
+    public static List<File> find(Path sourceDirectory) throws IOException
+    {
+        LOGGER.info("Starting to import resources from filesystem by path {}", sourceDirectory);
+
+        try (Stream<Path> paths = Files.walk(sourceDirectory))
+        {
+            return paths.map(Path::toFile)
+                        .filter(File::isFile)
+                        .filter(JsonResourceFinder::filterJsonFile)
+                        .collect(Collectors.toList());
+        }
+    }
+
+    private static boolean filterJsonFile(File file)
+    {
+        boolean json = isJsonFile(file);
+        if (!json)
+        {
+            LOGGER.info("Content of file '{}' is not JSON", file.getAbsolutePath());
+        }
+        return json;
+    }
