+public final class JsonResourceReader
+{
+    private static final Logger LOGGER = LoggerFactory.getLogger(JsonResourceReader.class);
+
+    private static final JsonUtils JSON_UTILS = new JsonUtils();
+
+    private JsonResourceReader()
+    {
+    }
+
+    public static List<FileEntry> readFrom(Path sourceDirectory) throws IOException
+    {
+        LOGGER.info("Starting to import resources from filesystem by path {}", sourceDirectory);
+
+        try (Stream<Path> paths = Files.walk(sourceDirectory))
+        {
+            return paths.map(Path::toFile)
+                        .filter(File::isFile)
+                        .map(f -> new FileEntry(f.getAbsolutePath(), readFileQuietly(f)))
+                        .filter(JsonResourceReader::filterJsonFile)
+                        .collect(Collectors.toList());
+        }
+    }
+
+    private static boolean filterJsonFile(FileEntry fileEntry)
+    {
+        boolean json = JSON_UTILS.isJson(fileEntry.getContent());
+        if (!json)
+        {
+            LOGGER.atInfo().addArgument(fileEntry::getPath).log("Content of file '{}' is not JSON");
+        }
+        return json;
