+
+        try {
+            startTenantFlow(tenantDomain);
+
+            for (String url : urls) {
+                if (isInvalidUrl(url)) {
+                    if (log.isErrorEnabled()) {
+                        log.error(String.format("%s is an invalid URL.", url));
+                    }
+                    throw new CORSServiceException(String.format("%s is an invalid URL.", url));
+                }
+                if (!tenantHasCORSUrl(url)) {
+                    if (log.isDebugEnabled()) {
+                        log.debug(String.format(String.format("Tenant %s doesn't have %s as a CORS URL.",
