+                    }
+                    throw handleClientException(ERROR_CODE_ORIGIN_NOT_PRESENT, tenantDomain, corsOrigin.getUrl());
+                }
+            }
+
+            for (CORSOrigin corsOrigin : corsOrigins) {
+                Attribute attribute = new CORSOriginToAttribute().apply(corsOrigin);
