+package org.zkoss.zktest.zats.test2;
+
+import org.junit.Assert;
+import org.junit.Test;
+import org.zkoss.zktest.zats.WebDriverTestCase;
+import org.zkoss.zktest.zats.ztl.JQuery;
+
+public class F91_ZK_4544Test extends WebDriverTestCase {
+	@Test
+	public void radioTest() {
+		connect();
+		
+		JQuery radio = jq(".z-radio").eq(0);
+		JQuery radioDisabledButton = jq("@button").eq(0);
+		
+		Assert.assertTrue(radio.hasClass("z-radio-off"));
+		
+		click(jq(".z-radio-content").eq(0));
+		waitResponse();
+		Assert.assertTrue(radio.hasClass("z-radio-on"));
+		
+		click(jq(".z-radio-content").eq(1));
+		waitResponse();
+		Assert.assertFalse(radio.hasClass("z-radio-on"));
+		Assert.assertTrue(jq(".z-radio").eq(1).hasClass("z-radio-on"));
+		
+		click(radioDisabledButton);
+		waitResponse();
+		Assert.assertTrue(radio.hasClass("z-radio-disabled"));
+		
+		click(radioDisabledButton);
+		waitResponse();
+		Assert.assertFalse(radio.hasClass("z-radio-disabled"));
+	}
+	
+	@Test
+	public void checkboxTest() {
+		connect();
+		
+		JQuery checkbox = jq(".z-checkbox").eq(0);
+		JQuery checkboxContent = jq(".z-checkbox-content").eq(0);
+		JQuery checkboxDisabledButton = jq("@button").eq(1);
+		
+		Assert.assertTrue(checkbox.hasClass("z-checkbox-off"));
