+		this.output.collect(new StreamRecord<>(new PartitionCommitInfo(
+				checkpoint,
+				getRuntimeContext().getIndexOfThisSubtask(),
+				getRuntimeContext().getNumberOfParallelSubtasks(),
+				new ArrayList<>(this.partitions))));
+		this.partitions.clear();
+	}
+
