+		super.snapshotState(context);
+
+		expiredFilesState.clear();
+		expiredFiles.put(context.getCheckpointId(), new ArrayList<>(currentExpiredFiles));
+		expiredFilesState.add(expiredFiles);
+		currentExpiredFiles.clear();
+	}
