+/**
+ * The {@code IntervalST} class represents read-only balanced binary interval search tree map (from intervals to values)
+ */
+public class IntervalST<Value> {
+
+  // List representation of bst with root at index 0. For node with index x, it's left child index is (2x+1), right child index is (2x+2)
+  //
+  // A node with index x is represented as following structure
+  //   {
+  //        Interval _interval = _intervals[x];
+  //        List<Value> _values = _values[x];
+  //        long _max = _max[x];                   // max interval right end of subtree rooted at this node
+  //    }
+  private final List<Interval> _intervals;
+  private final List<List<Value>> _values;
+  private final List<Long> _maxs;
+
+  public IntervalST(Map<Value, Interval> valueToIntervalMap) {
+    HashMap<Interval, List<Value>> intervalToValuesMap = new HashMap<>();
+    for (Map.Entry<Value, Interval> entry : valueToIntervalMap.entrySet()) {
+      List<Value> vals = intervalToValuesMap.putIfAbsent(entry.getValue(), new ArrayList<>());
+      intervalToValuesMap.get(entry.getValue()).add(entry.getKey());
+    }
+
+    List<Interval> sortedIntervals = new ArrayList<>(intervalToValuesMap.keySet());
+    Collections.sort(sortedIntervals);
+
+    _intervals = new ArrayList<>(sortedIntervals.size());
+    buildIntervalTree(sortedIntervals, _intervals);
+
+    _values = new ArrayList<>(_intervals.size());
+    for (Interval interval: _intervals) {
+      if (interval == null) {
+        _values.add(null);
+      } else {
+        _values.add(intervalToValuesMap.get(interval));
+      }
+    }
+
+    _maxs = new ArrayList<>(_intervals.size());
+    buildAuxiliaryInfo();
+  }
+
+  private void buildIntervalTree(List<Interval> sortedIntervals, List<Interval> res) {
+    // Build interval bst by bfs, the root for each subtree will be the median interval.
+    // A typical balanced tree
+    //                              [10, 20]
+    //                              /       \
+    //                       [8, 15]        [12, 20]
+    //                          /            /
+    //                   [5, 10]       [10, 30]
+    //
+    // will be represented as { [10, 20], [8, 15], [12, 20], [5, 10], null, [10, 30] }
+    if (sortedIntervals.size() == 0) {
+      return;
+    }
