+            public float score() {
+                return 1;
+            }
+        };
+    }
+
+    @Override
+    public HttpExecutionStrategy influenceStrategy(final HttpExecutionStrategy strategy) {
+        return config.strategyInfluencer.influenceStrategy(strategy);
+    }
+
