+                strategyInfluencer = (HttpExecutionStrategyInfluencer) rawFactory;
+            } else if(rawFactory instanceof RoundRobinLoadBalancerFactory) {
+                strategyInfluencer = strategy -> strategy; // RoundRobinLoadBalancer is non-blocking.
