+        Path recipeDirectoryPath = Paths.get(request.getRecipeDirectoryPath());
+        Path kernelPackageStorePath = kernel.getPackageStorePath();
+        Path kernelRecipeDirectoryPath = kernelPackageStorePath.resolve("recipes");
+        try {
+            copyFolderRecursively(recipeDirectoryPath, kernelRecipeDirectoryPath);
+        } catch (IOException e) {
+            logger.atError().setCause(e).kv("Recipe Directory path", recipeDirectoryPath)
+                    .log("Caught exception while updating the recipes");
+            throw new InvalidRecipesDirectoryPathError(e.getMessage());
+        }
+
+        Path artifactsDirectoryPath = Paths.get(request.getArtifactDirectoryPath());
+        Path kernelArtifactsDirectoryPath = kernelPackageStorePath.resolve("artifacts");
+        try {
+            copyFolderRecursively(artifactsDirectoryPath, kernelArtifactsDirectoryPath);
+        } catch (IOException e) {
+            logger.atError().setCause(e).kv("Recipe Directory path", recipeDirectoryPath)
+                    .log("Caught exception while updating the recipes");
+            throw new InvalidArtifactsDirectoryPathError(e.getMessage());
+        }
+    }
