+public class AllCodeSystemSearchRequest implements Request<ServiceProvider, CodeSystems> {
+
+	private static final long serialVersionUID = 1L;
+	
+	private final Collection<String> ids;
+	private final List<String> fields;
+	private final String expand;
+	
+	AllCodeSystemSearchRequest(final Collection<String> ids, final List<String> fields, final String expand) {
+		this.ids = ids;
+		this.fields = fields;
+		this.expand = expand;
+	}
+
+	@Override
+	public CodeSystems execute(ServiceProvider context) {
+		final List<CodeSystem> codeSystemList = Lists.newArrayList();
+
+		final Repositories repositories = RepositoryRequests.prepareSearch()
+				.all()
+				.build()
+				.execute(context);
+		
+		repositories.forEach(repositoryInfo -> {
+			CodeSystems css = CodeSystemRequests.prepareSearchCodeSystem()
+					.all()
+					.filterByIds(ids)
+					.setFields(fields)
+					.setExpand(expand)
+					.build(repositoryInfo.id())
+					.getRequest()
+					.execute(context);
+			
+			codeSystemList.addAll(css.getItems());
+		});
+		
+		return new CodeSystems(codeSystemList, null, 50, codeSystemList.size());

