File pluginDir = new File(systemArgs.getOption(ProgramOptionConstants.PLUGIN_DIR));
File pluginArchiveFile = new File("artifacts_archive.jar");
if (!pluginArchiveFile.exists()) {
  if (!pluginDir.isDirectory()) {
    // this shouldn't happen, throw exception
    throw new IllegalStateException(...);
  }
  // Archive the plugin directory content locally
  pluginArchiveFile = new File(tempDir, "artifacts_archive.jar");
  BundleJarUtil.createJar(localDir, pluginArchiveFile);
}

// Localize all plugins to the program containers.
// We localize to two target paths, one get expanded to a "artifacts" directory, 
// and one keep it as jar "artifacts_archive.jar" for further localization to other containers in YARN
localizeResources.put("artifacts", new LocalizeResource(pluginArchiveFile, true));
localizeResources.put("artifacts_archive.jar", new LocalizeResource(pluginArchiveFile, false));

newSystemArgs.put(ProgramOptionConstants.PLUGIN_DIR, "artifacts");
newSystemArgs.put(ProgramOptionConstants.PLUGIN_ARCHIVE, "artifacts_archive.jar");

