+import cz.metacentrum.perun.core.api.exceptions.ConsistencyErrorException;
+import cz.metacentrum.perun.core.api.exceptions.WrongAttributeAssignmentException;
+import cz.metacentrum.perun.core.api.exceptions.WrongAttributeValueException;
+import cz.metacentrum.perun.core.api.exceptions.WrongReferenceAttributeValueException;
+import cz.metacentrum.perun.core.impl.PerunSessionImpl;
+import cz.metacentrum.perun.core.implApi.modules.attributes.GroupAttributesModuleAbstract;
+import cz.metacentrum.perun.core.implApi.modules.attributes.GroupAttributesModuleImplApi;
+
+import java.util.ArrayList;
+import java.util.HashMap;
+import java.util.HashSet;
+import java.util.List;
+import java.util.Map;
+import java.util.Set;
+import java.util.regex.Pattern;
+
+/**
+ * AD Name module
+ *
+ * Only one group can have this name set for one facility no matter the OU.
+ * If group is assigned to more than 1 resource with OU set on the same facility, this state is forbidden.
+ *
+ * @author Michal Stava &lt;stavamichal@gmail.com&gt;
+ */
+public class urn_perun_group_attribute_def_def_adName_o365mu extends GroupAttributesModuleAbstract implements GroupAttributesModuleImplApi {
+
+	private static final String A_R_D_AD_OU_NAME = AttributesManager.NS_RESOURCE_ATTR_DEF + ":adOuName";
+
+	private static final Pattern pattern = Pattern.compile("(\\w|-|\\.)*");
+
+	@Override
+	public void checkAttributeSyntax(PerunSessionImpl sess, Group group, Attribute attribute) throws WrongAttributeValueException {
+		//Attribute can be null
+		if (attribute.getValue() == null) return;
+
+		if (!pattern.matcher(attribute.valueAsString()).matches()) {
+			throw new WrongAttributeValueException(attribute, "Invalid attribute adName value. It should contain only letters, digits, hyphens, underscores or dots.");
+		}
+	}
+
+	@Override
+	public void checkAttributeSemantics(PerunSessionImpl sess, Group group, Attribute attribute) throws WrongReferenceAttributeValueException, WrongAttributeAssignmentException {
+		//Attribute can be null
+		if(attribute.getValue() == null) {
+			return;
+		}
+
+		//Prepare map where keys are facilityIds (to be able to group them together) and values are all resources under facilityId
+		Map<Integer, Set<Resource>> mapOfResourcesByFacility = new HashMap<>();
+		for (Resource resource: sess.getPerunBl().getResourcesManagerBl().getAssignedResources(sess, group)) {
+			if (!mapOfResourcesByFacility.containsKey(resource.getFacilityId())) {
+				Set<Resource> setOfResourceWithSameFacilityId = new HashSet<>();
+				setOfResourceWithSameFacilityId.add(resource);
+				mapOfResourcesByFacility.put(resource.getFacilityId(), setOfResourceWithSameFacilityId);
+			} else {
+				mapOfResourcesByFacility.get(resource.getFacilityId()).add(resource);
