+        Group group = null;
+
+        Pattern linkPattern = compile("^.*/(" + REGEX_UUID + ")/?$");
+        Matcher matcher = linkPattern.matcher(groupLink);
+        if (matcher.matches()) {
+            group = groupService.find(context, UUID.fromString(matcher.group(1)));
+        }
+
+        return Optional.ofNullable(group);
+    }
