+    @Override
+    public boolean hasContent()
+    {
+        boolean hasContent = _rawContent != null;
+        if (LOG.isDebugEnabled())
+            LOG.debug("hasContent = {}", hasContent);
+        return hasContent;
+    }
+
+    @Override
+    public boolean isError()
