+                    }
+                }
+            } catch (JsonSyntaxException | NumberFormatException e) {
+                e.printStackTrace();
+                return new UpdateToken(BranchStatus.ERROR);
+            }
+        } catch (IOException e) {
+            e.printStackTrace();
+            return new UpdateToken(BranchStatus.ERROR);
+        }
+    }
