+                "    createEvent(" +
+                "        name:\"My added GraphQL event\"" +
+                "        where:\"Day 2\"" +
+                "        when:\"Saturday night\"" +
+                "        description:\"Studying GraphQL\"" +
+                "    ){" +
+                "        id" +
+                "        name" +
+                "        description" +
+                "    }" +
+                "}";
