        assertPlanHash(planner, plan2, PlanHashable.PlanHashKind.LEGACY, -447322749, -447322749);
        assertPlanHash(planner, plan2, PlanHashable.PlanHashKind.FOR_CONTINUATION, -40431985, -40431985);
        assertPlanHash(planner, plan2, PlanHashable.PlanHashKind.STRUCTURAL_WITHOUT_LITERALS, 453584239, 453584239);

