+    alterColumnOption(databaseId, "T1", "true");
+    alterColumnOption(databaseId, "T2", "true");
+    alterColumnOption(databaseId, "T3", "true");
+    writeAndVerify(client, Value.COMMIT_TIMESTAMP);
