+package com.graphhopper.reader.dem;
+
+/**
+ * This enum defines the interpolation method to use.
+ */
+public enum ElevationInterpolation {
+    NONE("none"), BILINEAR("bilinear");
+
+    private final String name;
+
+    ElevationInterpolation(String name) {
+        this.name = name;
+    }
+
+    @Override
+    public String toString() {
+        return name;
+    }
+
+    public static ElevationInterpolation find(String name) {
+        if (name == null || name.isEmpty())
+            return NONE;
+
+        for (ElevationInterpolation interpolation : values()) {
+            if (interpolation.name().equalsIgnoreCase(name)) {
+                return interpolation;
+            }
+        }
+
+        return NONE;
+    }

