+
+    @Override
+    public int hashCode() {
+      return Objects.hash(target, helper, connectivityStateInfo, picker);
+    }
+
+    @Override
+    public String toString() {
+      return MoreObjects.toStringHelper(this)
+          .add("target", target)
+          .add("helper", helper)
+          .add("connectivityStateInfo", connectivityStateInfo)
+          .add("picker", picker)
