+    boolean hasData() {
+      return dataCacheEntry != null;
+    }
+
+    @Nullable
+    ChildPolicyWrapper getChildPolicyWrapper() {
+      if (!hasData()) {
+        return null;
+      }
+      return dataCacheEntry.getChildPolicyWrapper();
+    }
