+        try {
+          transitionToDataEntry(pendingCall.get());
+        } catch (Exception e) {
+          if (e instanceof ThrottledException) {
+            transitionToBackOff(Status.RESOURCE_EXHAUSTED.withCause(e));
+          } else {
+            transitionToBackOff(Status.fromThrowable(e));
+          }
+        }
+      }
+    }
