+      return dataCacheEntry.getChildPolicyWrapper();
+    }
+
+    @Nullable
+    public String getHeaderData() {
+      if (!hasData()) {
+        return null;
+      }
+      return dataCacheEntry.getHeaderData();
+    }
+
+    boolean hasError() {
+      return backoffCacheEntry != null;
+    }
+
+    boolean isPending() {
+      return pendingCacheEntry != null;
+    }
+
+    @Nullable
+    Status getStatus() {
+      if (!hasError()) {
+        return null;
