        span.setTag(TracingUtil.URL, request.url().toString());
        span.setTag(TracingUtil.HTTP_METHOD, request.method());
        span.setTag(TracingUtil.HEADERS, Json.silent(request.headers().toMultimap()).getValue());
        return span;
    }

